const input = $input.item.json;

// Validate required fields
if (!input.body?.document_id) {
  throw new Error('document_id is required');
}
if (!input.body?.profile_id) {
  throw new Error('profile_id is required');
}
if (!input.body?.rendered_image_paths || !Array.isArray(input.body.rendered_image_paths)) {
  throw new Error('rendered_image_paths array is required');
}
if (input.body.rendered_image_paths.length === 0) {
  throw new Error('At least one image path is required');
}

const mode = input.body.extraction_mode || 'deep';
if (!['fast', 'deep'].includes(mode)) {
  throw new Error('extraction_mode must be "fast" or "deep"');
}

return {
  json: {
    document_id: input.body.document_id,
    profile_id: input.body.profile_id,
    rendered_image_paths: input.body.rendered_image_paths,
    extraction_mode: mode
  }
};

// Simulate atomic document lock for idempotency
// In production: UPDATE core.document SET extraction_status = 'processing' 
// WHERE id = ? AND extraction_status = 'queued'
const docId = $input.item.json.document_id;

// Simulated lock result - assume document is available
const lockResult = {
  rowsAffected: 1,  // 1 = successfully locked, 0 = already processing
  documentId: docId
};

return {
  json: {
    ...$input.item.json,
    lock_acquired: lockResult.rowsAffected > 0
  }
};

// Fetch existing facts for deduplication
// In production: SELECT id, category_key, fact_title FROM core.fact 
// WHERE profile_id = ? AND source_document_id = ?

const profileId = $input.item.json.profile_id;
const documentId = $input.item.json.document_id;

// Simulated existing facts (empty for first extraction)
const existingFacts = [];

return {
  json: {
    ...$input.item.json,
    existing_facts: existingFacts
  }
};

// Download rendered images from Supabase Storage
// In production: HTTP GET to Supabase Storage API with service role key

const paths = $input.item.json.rendered_image_paths;
const images = [];

// Simulate downloaded images as base64 placeholders
for (const path of paths) {
  images.push({
    path: path,
    base64: 'iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAADUlEQVR42mNk+M9QDwADhgGAWjR9awAAAABJRU5ErkJggg==',
    mime: 'image/png'
  });
}

if (images.length === 0) {
  throw new Error('STORAGE_ERROR: Failed to download images');
}

return {
  json: {
    ...$input.item.json,
    images: images,
    batch_count: Math.ceil(images.length / 4)
  }
};

// Transform Latitude output to facts and filter duplicates
const inputItem = $input.first().json;
const existingFacts = $('fetch-existing-facts').first().json.existing_facts || [];

// Access the 'object' property from Latitude response
const extracted = inputItem.object || inputItem;

// Category mapping for 9 fast categories
const categories = [
  'professional_foundation',
  'impact_achievement', 
  'working_identity',
  'values_character',
  'growth_learning',
  'relationships_influence',
  'unique_dimensions',
  'operational_excellence',
  'market_presence'
];

const facts = [];

for (const cat of categories) {
  if (extracted[cat] && Array.isArray(extracted[cat])) {
    for (const factText of extracted[cat]) {
      if (factText && typeof factText === 'string' && factText.trim()) {
        facts.push({
          category_key: cat,
          fact_title: factText.substring(0, 100),
          fact_detail: factText,
          confidence: 0.85
        });
      }
    }
  }
}

// Filter out duplicates based on existing facts
const existingTitles = existingFacts.map(f => f.fact_title?.toLowerCase());
const newFacts = facts.filter(f => 
  !existingTitles.includes(f.fact_title?.toLowerCase())
);

return [{
  json: {
    document_id: $('validate-request').first().json.document_id,
    profile_id: $('validate-request').first().json.profile_id,
    facts: newFacts,
    total_extracted: facts.length,
    duplicates_filtered: facts.length - newFacts.length
  }
}];

// Insert facts into core.fact table
// In production: INSERT INTO core.fact (...) VALUES (...) RETURNING id

const data = $input.item.json;
const facts = data.facts || [];

// Simulate inserted facts with generated IDs
const insertedFacts = facts.map((fact, index) => ({
  id: `fact-${Date.now()}-${index}`,
  profile_id: data.profile_id,
  category_key: fact.category_key,
  fact_title: fact.fact_title,
  fact_detail: fact.fact_detail,
  extraction_source: 'cv_extraction',
  source_document_id: data.document_id,
  confidence: fact.confidence,
  created_at: new Date().toISOString()
}));

return {
  json: {
    ...data,
    inserted_facts: insertedFacts,
    facts_created: insertedFacts.length
  }
};

// Create fact relationships (job â†’ skill links)
const data = $input.item.json;
const facts = data.inserted_facts || [];

// Find job-related facts
const jobs = facts.filter(f => 
  f.category_key?.includes('professional_foundation') ||
  f.category_key?.includes('experience')
);

// Find skill-related facts  
const skills = facts.filter(f => 
  f.category_key?.includes('operational') ||
  f.fact_title?.toLowerCase().includes('skill')
);

// Create relationships
const relationships = [];
for (const job of jobs.slice(0, 5)) {
  for (const skill of skills.slice(0, 10)) {
    if (job.id && skill.id) {
      relationships.push({
        source_fact_id: job.id,
        target_fact_id: skill.id,
        relationship_type: 'used_skill'
      });
    }
  }
}

return {
  json: {
    ...data,
    relationships_created: relationships.length
  }
};

// Queue facts for Mem0 synchronization
// In production: INSERT INTO core.memory_sync_outbox (...)

const data = $input.item.json;
const facts = data.inserted_facts || [];

// Simulate outbox entries
const outboxEntries = facts.map(f => ({
  entity_type: 'fact',
  entity_id: f.id,
  operation: 'add',
  payload: {
    text: `${f.fact_title}: ${f.fact_detail || ''}`,
    metadata: { category: f.category_key }
  },
  sync_status: 'pending'
}));

return {
  json: {
    ...data,
    mem0_queued: outboxEntries.length
  }
};

// Update document status to completed
// In production: UPDATE core.document SET extraction_status = 'completed' WHERE id = ?

const data = $input.item.json;

// Simulated update result
const updateResult = {
  document_id: data.document_id,
  extraction_status: 'completed',
  processed_at: new Date().toISOString(),
  extraction_metadata: {
    model: 'latitude',
    facts_created: data.facts_created
  }
};

return {
  json: {
    status: 'success',
    document_id: data.document_id,
    facts_created: data.facts_created,
    relationships_created: data.relationships_created || 0,
    mem0_queued: data.mem0_queued || 0
  }
};

