const input = $input.first().json.body || $input.first().json;

// Validate board_job_id
if (!input.board_job_id || !/^[0-9a-f-]{36}$/i.test(input.board_job_id)) {
  throw new Error('Invalid or missing board_job_id');
}

// Validate new_status against enum
const VALID_STATUSES = [
  'working', 'question', 'ready', 'submitted', 'withdrawn',
  'passed', 'interviewing', 'offered', 'accepted', 'rejected', 'archived'
];

if (!input.new_status || !VALID_STATUSES.includes(input.new_status)) {
  throw new Error(`Invalid new_status: ${input.new_status}. Must be one of: ${VALID_STATUSES.join(', ')}`);
}

return [{
  json: {
    board_job_id: input.board_job_id,
    new_status: input.new_status,
    trigger_source: input.trigger_source || 'unknown'
  }
}];

const currentState = $input.first().json;
const request = $('validate-request').first().json;

const currentStatus = currentState.board_status;
const newStatus = request.new_status;

// State machine definition
const VALID_TRANSITIONS = {
  'working':      ['question', 'ready', 'withdrawn'],
  'question':     ['working', 'withdrawn'],
  'ready':        ['submitted', 'withdrawn'],
  'submitted':    ['interviewing', 'rejected', 'passed'],
  'interviewing': ['offered', 'rejected', 'passed'],
  'offered':      ['accepted', 'rejected'],
  'rejected':     ['archived'],
  'passed':       ['archived'],
  'accepted':     ['archived'],
  'withdrawn':    ['archived'],
  'archived':     []
};

// Check if transition is valid
const allowedTargets = VALID_TRANSITIONS[currentStatus] || [];

if (!allowedTargets.includes(newStatus)) {
  // Check if it's a no-op (already in target state)
  if (currentStatus === newStatus) {
    throw new Error(`NO_OP: Already in status '${currentStatus}'`);
  }
  throw new Error(`INVALID_TRANSITION: Cannot transition from '${currentStatus}' to '${newStatus}'. Allowed: [${allowedTargets.join(', ')}]`);
}

// Determine if this transition requires prerequisite checks
const requiresQuestionCheck = (currentStatus === 'ready' && newStatus === 'submitted');

return [{
  json: {
    ...currentState,
    new_status: newStatus,
    current_status: currentStatus,
    trigger_source: request.trigger_source,
    requires_question_check: requiresQuestionCheck
  }
}];

// Get context from appropriate source
const context = $('validate-transition').first().json;
const newStatus = context.new_status;
const now = new Date().toISOString();

// Build update fields
const updateFields = {
  board_status: newStatus
};

// Set submitted_at only when transitioning TO submitted (and not already set)
if (newStatus === 'submitted' && !context.submitted_at) {
  updateFields.submitted_at = now;
}

// Set withdrawn_at only when transitioning TO withdrawn (and not already set)
if (newStatus === 'withdrawn' && !context.withdrawn_at) {
  updateFields.withdrawn_at = now;
}

// Determine notification needs
const NOTIFY_TRANSITIONS = {
  'ready': { type: 'application_ready', message: 'Your application is ready to submit' },
  'submitted': { type: 'application_submitted', message: 'Application submitted successfully' },
  'interviewing': { type: 'interview_scheduled', message: 'Great news! Interview scheduled' },
  'offered': { type: 'offer_received', message: 'You received a job offer!' },
  'rejected': { type: 'application_rejected', message: 'Application update' },
  'accepted': { type: 'offer_accepted', message: 'Congratulations on your new role!' }
};

const notification = NOTIFY_TRANSITIONS[newStatus] || null;

return [{
  json: {
    board_job_id: context.id,
    profile_id: context.profile_id,
    posting_id: context.posting_id,
    current_status: context.current_status,
    new_status: newStatus,
    update_fields: updateFields,
    should_notify: notification !== null,
    notification_type: notification?.type,
    notification_message: notification?.message,
    trigger_source: context.trigger_source
  }
}];

const input = $input.item.json;
const UUID_REGEX = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;

// Validate JWT
if (!input.jwtPayload?.sub) {
  throw new Error('AUTH_ERROR: Authentication required');
}

// Validate voice_transcript_id
if (!input.body?.voice_transcript_id) {
  throw new Error('VALIDATION_ERROR: voice_transcript_id required');
}
if (!UUID_REGEX.test(input.body.voice_transcript_id)) {
  throw new Error('VALIDATION_ERROR: voice_transcript_id must be valid UUID');
}

// Validate board_job_document_id
if (!input.body?.board_job_document_id) {
  throw new Error('VALIDATION_ERROR: board_job_document_id required');
}
if (!UUID_REGEX.test(input.body.board_job_document_id)) {
  throw new Error('VALIDATION_ERROR: board_job_document_id must be valid UUID');
}

return {
  json: {
    profile_id: input.jwtPayload.sub,
    voice_transcript_id: input.body.voice_transcript_id,
    board_job_document_id: input.body.board_job_document_id
  }
};

// Complex query requires Supabase REST API via Code node
const supabaseUrl = $env.SUPABASE_URL;
const supabaseKey = $env.SUPABASE_SERVICE_ROLE_KEY;
const documentId = $('validate-request').item.json.board_job_document_id;

// Query: Get latest version of selected document with ownership info
const query = `
  id,
  content_markdown,
  version_number,
  board_job_document_id,
  board_job_document:board_job_document_id(
    id,
    document_type,
    is_selected,
    board_job:board_job_id(
      id,
      profile_id,
      posting:posting_id(
        company_name,
        job_title
      )
    )
  )
`;

const response = await this.helpers.httpRequest({
  method: 'GET',
  url: `${supabaseUrl}/rest/v1/document_version`,
  qs: {
    select: query,
    'board_job_document_id': `eq.${documentId}`,
    order: 'version_number.desc',
    limit: 1
  },
  headers: {
    'apikey': supabaseKey,
    'Authorization': `Bearer ${supabaseKey}`,
    'Content-Type': 'application/json'
  },
  json: true
});

if (!response || response.length === 0) {
  throw new Error('DOCUMENT_NOT_FOUND: No document version found');
}

const version = response[0];
const doc = version.board_job_document;

if (!doc?.is_selected) {
  throw new Error('DOCUMENT_NOT_FOUND: Document not selected');
}

return {
  json: {
    current_version_id: version.id,
    content_markdown: version.content_markdown,
    version_number: version.version_number,
    document_type: doc.document_type,
    owner_profile_id: doc.board_job?.profile_id,
    company_name: doc.board_job?.posting?.company_name || 'the company',
    job_title: doc.board_job?.posting?.job_title || 'the position'
  }
};

