// Enhanced validation for AI-generated questions
const item = $input.first();

// Step 1: Validate input structure
if (!item || !item.json) {
  console.error('No input data received from AI');
  return [];
}

// Step 2: Validate conversation structure
if (!item.json.conversation || !Array.isArray(item.json.conversation)) {
  console.error('Invalid conversation structure');
  return [];
}

const conversation = item.json.conversation;
if (conversation.length === 0) {
  console.error('Empty conversation array');
  return [];
}

const lastMessage = conversation[conversation.length - 1];
if (!lastMessage || !lastMessage.content || !lastMessage.content[0]) {
  console.error('Invalid message structure in conversation');
  return [];
}

// Step 3: Parse AI response with error handling
let questionsArray;
try {
  const jsonString = lastMessage.content[0].text;
  if (!jsonString) {
    console.error('Empty response text from AI');
    return [];
  }

  questionsArray = JSON.parse(jsonString);

  // Validate it's an array
  if (!Array.isArray(questionsArray)) {
    console.error('AI response is not an array');
    return [];
  }

  if (questionsArray.length === 0) {
    console.log('AI returned empty questions array');
    return [];
  }
} catch (error) {
  console.error('Failed to parse AI response:', error.message);
  return [];
}

// Step 4: Validate and filter questions
const validQuestions = questionsArray
  .filter((q, index) => {
    // Check required fields exist
    if (!q.question && !q.question_text) {
      console.warn(`Question ${index}: Missing question text`);
      return false;
    }

    if (!q.context) {
      console.warn(`Question ${index}: Missing context`);
      return false;
    }

    // For multiple_choice questions, validate options array
    const questionType = q.question_type || 'multiple_choice';
    if (questionType === 'multiple_choice') {
      if (!Array.isArray(q.options)) {
        console.warn(`Question ${index}: Missing options array for multiple_choice`);
        return false;
      }

      if (q.options.length < 2) {
        console.warn(`Question ${index}: Needs at least 2 options, got ${q.options.length}`);
        return false;
      }
    }

    return true;
  })
  .map(q => ({
    json: {
      question: q.question_text || q.question,
      question_text: q.question_text || q.question,
      question_type: q.question_type || 'multiple_choice',
      context: q.context,
      options: q.options || [],
      // Pass through user_id for reference
      _source: 'ai_generated'
    }
  }));

console.log(`Validated ${validQuestions.length} out of ${questionsArray.length} questions`);

return validQuestions;

// Get the incoming item's JSON data.
const item = $input.first();
if (!item || !item.json) {
  throw new Error('No input data received');
}

const data = item.json;

// This array will hold all the pieces of our final Markdown string.
const markdownParts = [];

// Helper function to format category names like "professional_foundation" into "Professional Foundation".
const formatCategoryName = (name) => {
  if (!name) return '';
  return name
    .split('_')
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ');
};

try {
  // --- 1. CV / Profile Section ---
  markdownParts.push('# Candidate Profile');
  if (data.profile && data.profile.full_text) {
    markdownParts.push(data.profile.full_text);
  }
  markdownParts.push('\n---\n');

  // --- 2. Professional Facts Section ---
  markdownParts.push('## Professional Facts');
  const factsByCategory = data.facts?.by_category;

  if (factsByCategory) {
    for (const [category, factList] of Object.entries(factsByCategory)) {
      markdownParts.push(`\n**${formatCategoryName(category)}**`);

      if (factList && factList.length > 0) {
        for (const factObject of factList) {
          markdownParts.push(`- ${factObject.fact}`);
        }
      }
    }
  }
  markdownParts.push('\n---\n');

  // --- 3. Questions & Answers Section ---
  markdownParts.push('## Questions & Answers');
  const qas = data.questions_and_answers;

  if (qas && qas.length > 0) {
    for (const qa of qas) {
      markdownParts.push(`\n**Q:** ${qa.question_text}`);

      const textAnswer = qa.answer?.text_answer || 'No text answer provided.';
      markdownParts.push(`**A:** ${textAnswer}`);

      if (qa.answer?.selected_options && qa.answer.selected_options.length > 0) {
        markdownParts.push(`\n*Selected Options:*`);
        for (const option of qa.answer.selected_options) {
          markdownParts.push(`- ${option}`);
        }
      }
    }
  }

  // --- 4. Final Assembly ---
  const finalMarkdownString = markdownParts.join('\n');

  // Return array of items with json property
  return [{
    json: {
      markdown_profile: finalMarkdownString,
      user_id: data.user_id || null
    }
  }];
} catch (error) {
  throw new Error(`Failed to build markdown: ${error.message}`);
}

// Get the first (and only) incoming item.
const item = $input.first();

// 1. Navigate to the last message in the conversation array.
const conversation = item.json.conversation;
const lastMessage = conversation[conversation.length - 1];

// 2. Extract the text, which is a JSON string.
const jsonString = lastMessage.content[0].text;

// 3. Parse the JSON string into a usable JavaScript object.
// The string "{ \"category\": [\"fact1\", \"fact2\"] }" becomes a real object.
const factsByCategory = JSON.parse(jsonString);

// 4. Create an empty array to hold our new, individual items.
const newItems = [];

// 5. This is the core logic: Loop through each category and its facts.
// Object.entries() turns the object into an array like: [ ['categoryName', ['fact1', 'fact2']], ... ]
for (const [category, facts] of Object.entries(factsByCategory)) {

  // Now, for each category, loop through its array of fact strings.
  for (const fact of facts) {

    // For every single fact, create a new n8n item object...
    const newItem = {
      json: {
        category: category, // The name of the category (e.g., "professional_foundation")
        fact: fact        // The individual fact string
      }
    };

    // ...and add it to our list.
    newItems.push(newItem);
  }
}

// 6. Return the full array of new items.
// n8n will process this and create a separate output for each fact.
return newItems;

// Get the first (and only) incoming item.
const item = $input.first();

// --- Configuration & Helpers ---

// A constant Set for very fast lookups of allowed country codes.
const ALLOWED_COUNTRIES = new Set([
  "ar", "au", "at", "bh", "be", "br", "ca", "cl", "cn", "co", "cr", "cz", "dk", "ec", "eg", "fi", "fr", "de", "gr", "hk", "hu", "in", "id", "ie", "il", "it", "jp", "kw", "lu", "my", "mx", "ma", "nl", "nz", "ng", "no", "om", "pk", "pa", "pe", "ph", "pl", "pt", "qa", "ro", "sa", "sg", "za", "kr", "es", "se", "ch", "tw", "th", "tr", "ua", "ae", "uk", "us", "uy", "ve", "vn"
]);

// Helper function to find the closest number in an allowed list.
const roundToNearestAllowed = (inputValue, allowedValues) => {
  const inputNum = parseInt(inputValue, 10);
  if (isNaN(inputNum)) return String(allowedValues[allowedValues.length - 1]);
  if (allowedValues.includes(inputNum)) return String(inputNum);
  const closest = allowedValues.reduce((a, b) => Math.abs(b - inputNum) < Math.abs(a - inputNum) ? b : a);
  return String(closest);
};

const allowedDateValues = [1, 3, 7, 14];

// --- Main Logic ---

// 1. Navigate to the last message in the conversation array.
const conversation = item.json.conversation;
const lastMessage = conversation[conversation.length - 1];

// 2. Extract the text, which contains the JSON string.
const jsonString = lastMessage.content[0].text;

// 3. Parse the JSON string into a usable JavaScript object.
const parsedData = JSON.parse(jsonString);

// 4. Get the array of search queries from the parsed object.
const searchQueries = parsedData.search_queries;

// 5. Map over the array to transform each query.
const newItems = searchQueries.map(query => {

  // a. Use destructuring to remove 'reasoning' and 'importance'.
  const { reasoning, importance, ...rest } = query;

  // b. NEW: Validate and transform the 'country' property.
  // First, safely convert the country to lowercase. Using (rest.country || '') prevents errors if it's null.
  const countryCode = (rest.country || '').toLowerCase();

  // Check if the lowercase code is in our allowed Set. If not, default to 'us'.
  if (ALLOWED_COUNTRIES.has(countryCode)) {
    rest.country = countryCode;
  } else {
    rest.country = 'us';
  }


  // d. Round the 'datePosted' field to the nearest allowed value.
  if (rest.datePosted) {
    rest.datePosted = roundToNearestAllowed(rest.datePosted, allowedDateValues);
  }

  // e. Create a new n8n item with the transformed object.
  return {
    json: rest 
  };
});

// 6. Return the array of new items.
return newItems;

const input = $input.item.json;

if (!input.jwtPayload?.sub) {
  throw new Error('Authentication required');
}

const fullName = input.body?.full_name?.trim();
if (!fullName || fullName.length < 2) {
  throw new Error('Name must be at least 2 characters');
}

return {
  json: {
    profile_id: input.jwtPayload.sub,
    full_name: fullName,
    search_query: `site:linkedin.com/in ${fullName}`
  }
};

const inputItem = $input.first().json;

// FIX: Access the 'object' property where the array of candidates actually lives.
// If 'object' doesn't exist, we default to an empty array to prevent errors.
const candidates = Array.isArray(inputItem.object) ? inputItem.object : [];

return candidates.map(candidate => {
  let locationCity = null;
  let locationCountry = null;

  if (candidate.location) {
    const parts = candidate.location.split(',').map(s => s.trim());
    locationCity = parts[0] || null;
    // Only assign country if a second part exists
    locationCountry = parts.length > 1 ? parts[1] : null;
  }

  return {
    json: {
      linkedin_url: candidate.linkedin_url,
