// Code Node - Validate Webhook Input
// Run Once for All Items

const items = $input.all();
let validatedItems = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i].json;
  let errors = [];
  let warnings = [];

  try {
    // ============================================
    // 1. STRUCTURE VALIDATION
    // ============================================

    if (!item.body) {
      errors.push('Missing request body');
    }

    if (!item.headers) {
      errors.push('Missing request headers');
    }

    if (!item.jwtPayload) {
      errors.push('Missing JWT payload - authentication required');
    }

    // ============================================
    // 2. AUTHORIZATION

 VALIDATION
    // ============================================

    if (item.headers && !item.headers.authorization) {
      errors.push('Missing Authorization header');
    } else if (item.headers && item.headers.authorization) {
      // Check if it's a Bearer token
      if (!item.headers.authorization.startsWith('Bearer ')) {
        errors.push('Invalid authorization format - must be "Bearer <token>"');
      }
    }

    // ============================================
    // 3. JWT PAYLOAD VALIDATION
    // ============================================

    if (item.jwtPayload) {
      // Check required JWT fields
      const requiredJwtFields = ['sub', 'email', 'role', 'exp', 'iat'];
      requiredJwtFields.forEach(field => {
        if (!item.jwtPayload[field]) {
          errors.push(`Missing required JWT field: ${field}`);
        }
      });

      // Validate JWT expiration
      if (item.jwtPayload.exp) {
        const now = Math.floor(Date.now() / 1000); // Current time in seconds
        const expiration = item.jwtPayload.exp;

        if (expiration < now) {
          errors.push(`JWT token expired at ${new Date(expiration * 1000).toISOString()}`);
        } else {
          // Calculate time until expiration
          const timeUntilExpiry = expiration - now;
          const hoursUntilExpiry = (timeUntilExpiry / 3600).toFixed(2);

          // Warn if token expires soon (< 1 hour)
          if (timeUntilExpiry 

< 3600) {
            warnings.push(`JWT token expires in ${hoursUntilExpiry} hours`);
          }
        }
      }

      // Validate email format
      if (item.jwtPayload.email) {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(item.jwtPayload.email)) {
          errors.push(`Invalid email format: ${item.jwtPayload.email}`);
        }
      }

      // Check role
      if (item.jwtPayload.role && item.jwtPayload.role !== 'authenticated') {
        warnings.push(`Unexpected role: ${item.jwtPayload.role}`);
      }

      // Check if anonymous user
      if (item.jwtPayload.is_anonymous === true) {
        errors.push('Anonymous users are not allowed');
      }
    }

    // ============================================
    // 4. BODY/PAYLOAD VALIDATION
    // ============================================

    if (item.body) {
      // Check if URL exists
      if (!item.body.url) {
        errors.push('Missing required field: url');
      } else {
        const url = item.body.url;

        // Validate URL format
        try {
          const urlObj = new URL(url);

          // Check if it's a LinkedIn URL
          if (!urlObj.hostname.includes('linkedin.com')) {
            errors.push(`Invalid LinkedIn URL - must be from linkedin.com domain. Got: ${urlObj.hostname}`);
          }

          // Check if it's a profile URL
          if (!url.includes('/in/')) {
            errors.push('Invalid LinkedIn profile URL - must contain "/in/" path');
          }

          // Extract username from LinkedIn

 URL
          const linkedinUsername = url.match(/\/in\/([^\/\?]+)/);
          if (linkedinUsername && linkedinUsername[1]) {
            item.linkedinUsername = linkedinUsername[1];
          } else {
            warnings.push('Could not extract LinkedIn username from URL');
          }

          // Check for valid protocol
          if (urlObj.protocol !== 'https:' && urlObj.protocol !== 'http:') {
            errors.push(`Invalid URL protocol: ${urlObj.protocol}`);
          }

        } catch (urlError) {
          errors.push(`Invalid URL format: ${url} - ${urlError.message}`);
        }
      }
    }

    // ============================================
    // 5. WEBHOOK METADATA VALIDATION
    // ============================================

    if (!item.webhookUrl) {
      warnings.push('Missing webhook URL metadata');
    }

    if (!item.executionMode) {
      warnings.push('Missing execution mode metadata');
    } else if (item.executionMode === 'test') {
      warnings.push('Running in TEST mode');
    }

    // ============================================
    // 6. PREPARE OUTPUT
    // ============================================

    const isValid = errors.length === 0;

    // Add validation results to item
    item.validation = {
      isValid: isValid,
      errors: errors,
      warnings: warnings,
      validatedAt: new Date().toISOString(),
      errorCount: errors.length,
      warningCount: warnings.length
    };

    // Add extracted user info
    if (item.jwtPayload) {
      item.user

 = {
        id: item.jwtPayload.sub,
        email: item.jwtPayload.email,
        role: item.jwtPayload.role,
        sessionId: item.jwtPayload.session_id
      };
    }

    // Log validation results
    console.log(`Validation Result for item ${i}:`, {
      isValid: isValid,
      errors: errors,
      warnings: warnings
    });

    // If invalid, throw error to stop workflow
    if (!isValid) {
      const errorMessage = `Validation failed with ${errors.length} error(s): ${errors.join('; ')}`;
      console.error(errorMessage);

      // Option 1: Throw error to stop workflow
      // throw new Error(errorMessage);

      // Option 2: Return with validation status (allows workflow to continue)
      item.validation.errorMessage = errorMessage;
    }

    validatedItems.push({ json: item });

  } catch (error) {
    console.error(`Error processing item ${i}:`, error);

    // Add error to item
    item.validation = {
      isValid: false,
      errors: [`Validation error: ${error.message}`],
      warnings: [],
      validatedAt: new Date().toISOString(),
      errorCount: 1,
      warningCount: 0
    };

    validatedItems.push({ json: item });

    // Optionally re-throw to stop workflow
    // throw error;
  }
}

// Return all validated items
return validatedItems;

