const MONTH_NAMES = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];

// === UTILITIES ===
function escapeXml(t, escapeQuotes = true) { if(!t||typeof t!=='string')return''; let r = t.replace(/&/g,'&amp;').replace(/</g,'&lt;'); if(escapeQuotes) r = r.replace(/"/g,'&quot;'); return r; }
function decodeHtmlEntities(t) { if(!t||typeof t!=='string')return''; return t.replace(/\\"/g, '"').replace(/&apos;/g, "'").replace(/&#39;/g, "'").replace(/&quot;/g, '"').replace(/&lt;/g, '<').replace(/&gt;/g, '>').replace(/&amp;/g, '&').replace(/\u00A0/g, ' ').replace(/\xA0/g, ' ').replace(/[\u2018\u2019]/g, "'").replace(/[\u201C\u201D]/g, '"').replace(/[\u201C\u201D]/g, '"').replace(/[\u2018\u2019]/g, "'").replace(/[\u201C\u201D]/g, '"').replace(/[\u2018\u2019]/g, "'").replace(/[\u201C\u201D]/g, '"'); }
function replaceNewlinesWithTabs(t) { if(!t||typeof t!=='string')return''; return t.replace(/\n/g, '\t\t\t'); }
function formatNumber(n) { if(n===null||n===undefined)return"0"; if(typeof n==='string'){if(n.includes('+'))return n;n=parseFloat(n.replace(/,/g,''));} if(isNaN(n))return"0"; return n.toLocaleString('en-US'); }
function formatDate(d,def="") { if(!d||typeof d!=='object'||!d.year)return def; const y=parseInt(d.year,10); if(isNaN(y)||y===0)return def; let r=""; if(d.month&&d.month>0&&d.month<=12)r=`${MONTH_NAMES[d.month-1]} `; return r+y; }
function formatPeriodAttrs(d) { if(!d||typeof d!=='object'||!d.year)return null; const y=parseInt(d.year,10); if(isNaN(y)||y===0)return null; const attrs={year:String(y)}; if(d.month&&d.month>0&&d.month<=12)attrs.month=String(d.month); return attrs; }
function formatDateISO(ds) { if(!ds||typeof ds!=='string')return null; try{const d=new Date(ds);if(isNaN(d.getTime()))return null;return d.toISOString().replace(/\.\d{3}Z$/,'Z');}catch{return null;} }
function isValidUrl(u) { return u&&typeof u==='string'&&/^https?:\/\/.+\..+/.test(u); }
function safeArray(i) { if(!i)return[]; if(Array.isArray(i))return i; if(typeof i==='object'&&Object.keys(i).length===0)return[]; if(typeof i==='object')return[i]; return[]; }
function safeString(v,d='') { if(v===null||v===undefined)return d; if(typeof v==='string')return v; if(typeof v==='number'||typeof v==='boolean')return String(v); if(typeof v==='object'){if(v.text)return String(v.text);if(v.name)return String(v.name);if(v.title)return String(v.title);} return d; }
function truncateText(t,m) { if(!t||typeof t!=='string')return""; if(t.length<=m)return t; let c=m; const ls=t.lastIndexOf(' ',m); if(ls>m*0.8)c=ls; return t.substring(0,c).trim()+"..."; }
function formatDateRange(s,e,p=true) { const st=formatDate(s),en=formatDate(e,p?'Present':''); if(!st&&!en)return''; if(!st)return en; if(!en)return st; return `${st} â€“ ${en}`; }
function getBestImage(arr,pw=800) { 
  if(!Array.isArray(arr)||arr.length===0)return null; 
  // Prefer images with feedshare-shrink_800 in URL (for posts)
  for(const i of arr){
    if(i&&i.url&&i.url.includes('feedshare-shrink_800')&&isValidUrl(i.url)) return i;
  }
  // Fall back to finding closest width
  let best=null,diff=Infinity; 
  for(const i of arr){if(!i||!i.url)continue;const w=parseInt(i.width,10)||0,d=Math.abs(w-pw);if(d<diff){diff=d;best=i;}} 
  return best&&isValidUrl(best.url)?best:null; 
}
function extractUrl(i) { if(!i)return null; if(typeof i==='string'&&isValidUrl(i))return i; if(typeof i==='object'){const u=i.url||i.link||i.href;if(u&&isValidUrl(u))return u;} return null; }
function getCurrentDate() { const n=new Date(); return `${n.getFullYear()}-${String(n.getMonth()+1).padStart(2,'0')}-${String(n.getDate()).padStart(2,'0')}`; }
function getCurrentTimestamp() { return new Date().toISOString(); }
function deduplicatePositions(pos) { if(!Array.isArray(pos))return[]; const seen=new Set(),uniq=[]; for(const j of pos){if(!j||typeof j!=='object')continue;const k=`${j.companyId||''}-${j.title||''}-${j.start?.year||0}-${j.start?.month||0}`;if(!seen.has(k)){seen.add(k);uniq.push(j);}} return uniq; }
function validateDataStructure(data) { if(!data||typeof data!=='object')return{valid:false,error:"Root data is not an object",profile:null,posts:[]}; let p=data.data?.data||data.data||data.profile||data; if(!p||typeof p!=='object')return{valid:false,error:"Profile object missing",profile:null,posts:[]}; const hasN=p.firstName||p.lastName||p.name,hasU=p.username||p.publicIdentifier; if(!hasN&&!hasU)return{valid:false,error:"No identifying info",profile:null,posts:[]}; const posts=data.data?.posts||data.posts||p.posts||[]; return{valid:true,error:null,profile:p,posts:Array.isArray(posts)?posts:[]}; }
function xmlTag(tag,content,attrs={}) { const a=Object.entries(attrs).filter(([,v])=>v!==null&&v!==undefined&&v!=='').map(([k,v])=>`${k}="${escapeXml(String(v))}"`).join(' '); const o=a?`<${tag} ${a}>`:`<${tag}>`; if(content===null||content===undefined||content==='')return''; return `${o}${content}</${tag}>`; }
function xmlSelfClose(tag,attrs={}) { const a=Object.entries(attrs).filter(([,v])=>v!==null&&v!==undefined&&v!=='').map(([k,v])=>`${k}="${escapeXml(String(v))}"`).join(' '); return a?`<${tag} ${a}/>`:`<${tag}/>`; }

