      username: username || null,
      urn: profile.urn || profile.entityUrn || profile.id || null,
      headline: profile.headline || null,
      industry: profile.industry || profile.industryName || null,
      location: profile.geo?.full || profile.location?.name || null,
      country_code: profile.geo?.countryCode || null,
      followers: rootData.follower || profile.followerCount || 0,
      connections: rootData.connection || profile.connectionCount || 0,
      profile_url: username ? `https://www.linkedin.com/in/${username}` : null
    },
    stats: {
      posts_count: Array.isArray(posts) ? posts.length : 0,
      experience_count: safeArray(profile.fullPositions || profile.position || profile.positions).length,
      education_count: safeArray(profile.educations || profile.education).length,
      skills_count: safeArray(profile.skills).length,
      certifications_count: safeArray(profile.certifications || profile.licenses).length,
      projects_count: safeArray(profile.projects).length,
      volunteer_count: safeArray(profile.volunteer || profile.volunteerExperiences).length,
      publications_count: safeArray(profile.publications).length,
      patents_count: safeArray(profile.patents).length,
      honors_count: safeArray(profile.honors || profile.awards || profile.honorsAwards).length,
      courses_count: safeArray(profile.courses).length,
      has_email: !!(profile.email || profile.emailAddress),
      is_premium: !!(profile.isPremium || profile.premium),
