}

function buildProfile(profile, rootData) {
  const parts = [];
  const fn = profile.firstName || profile.name?.split(' ')[0] || "Unknown";
  const ln = profile.lastName || profile.name?.split(' ').slice(1).join(' ') || "";
  const fullName = `${fn} ${ln}`.trim();
  const un = profile.username || profile.publicIdentifier;
  const isPremium = profile.isPremium || profile.premium || false;
  const isCreator = profile.isCreator || profile.creator || false;

  parts.push(`  <profile>`);
  parts.push(`    <basic_info>`);
  if (un) parts.push(`      ${xmlTag('username', escapeXml(un))}`);
  if (un) parts.push(`      ${xmlTag('url', `https://www.linkedin.com/in/${un}`)}`);
  parts.push(`      ${xmlTag('first_name', escapeXml(fn))}`);
  parts.push(`      ${xmlTag('last_name', escapeXml(ln))}`);
  parts.push(`      ${xmlTag('full_name', escapeXml(fullName))}`);
  if (profile.headline) parts.push(`      ${xmlTag('headline', escapeXml(decodeHtmlEntities(profile.headline)))}`);
  if (isPremium) parts.push(`      ${xmlTag('is_premium', 'true')}`);
  if (isCreator) parts.push(`      ${xmlTag('is_creator', 'true')}`);
  parts.push(`    </basic_info>`);

  // Location
  const loc = profile.geo?.full || profile.location?.name || profile.geoLocationName;
  const cc = profile.geo?.countryCode, ct = profile.geo?.country, cy = profile.geo?.city;
  if (loc||ct||cy) {
    parts.push(``);
    parts.push(`    <location>`);
    if (loc) parts.push(`      ${xmlTag('full', escapeXml(loc))}`);
    if (cy) parts.push(`      ${xmlTag('city', escapeXml(cy))}`);
    if (ct) parts.push(`      ${xmlTag('country', escapeXml(ct))}`);
    if (cc) parts.push(`      ${xmlTag('country_code', escapeXml(cc))}`);
    parts.push(`    </location>`);
  }

  // Network (was stats)
  const fol = rootData.follower || profile.followerCount || 0;
  const con = rootData.connection || profile.connectionCount || 0;
  parts.push(``);
  parts.push(`    <network>`);
  parts.push(`      ${xmlTag('followers', String(fol))}`);
  parts.push(`      ${xmlTag('connections', String(con))}`);
  parts.push(`    </network>`);

  // Media
  let picObj = Array.isArray(profile.profilePictures) ? getBestImage(profile.profilePictures) : null;
  let pic = picObj ? picObj.url : (typeof profile.profilePicture==='string'&&isValidUrl(profile.profilePicture)?profile.profilePicture:null);
  let bgImgObj = Array.isArray(profile.backgroundImage) ? getBestImage(profile.backgroundImage, 1400) : null;
  const bgImg = bgImgObj ? bgImgObj.url : (typeof profile.backgroundImage==='string'&&isValidUrl(profile.backgroundImage)?profile.backgroundImage:null);
  if (pic || bgImg) {
    parts.push(``);
    parts.push(`    <media>`);
    if (pic) parts.push(`      ${xmlTag('profile_picture', escapeXml(pic), {width: '800', height: '800'})}`);
    if (bgImg) parts.push(`      ${xmlTag('background_image', escapeXml(bgImg), {width: '1400', height: '350'})}`);
    parts.push(`    </media>`);
  }

  // Locales
  const locales = profile.supportedLocales || profile.locales || [{language: 'en', country: 'US'}];
  if (locales.length > 0) {
    parts.push(``);
    parts.push(`    <locales>`);
    for (const l of locales) {
      const lang = l.language || l.lang || 'en';
      const country = l.country || 'US';
      parts.push(`      ${xmlSelfClose('locale', {language: lang, country: country})}`);
    }
    parts.push(`    </locales>`);
  }

  parts.push(`  </profile>`);
  return parts;
}

function buildExperience(positions) {
  const uniq = deduplicatePositions(positions);
  if (uniq.length === 0) return [];
  const parts = [`  <experience>`];
  for (const j of uniq) {
    if (!j) continue;
    const title = safeString(j.title||j.position||j.role, "Position");
    const cName = safeString(j.companyName||j.company?.name, "Company");
    const cUrl = j.companyURL||j.companyUrl||(j.companyUsername?`https://www.linkedin.com/company/${j.companyUsername}/`:null);
    const cUser = j.companyUsername||'', cId = j.companyId||'';
    const ind = j.companyIndustry||j.industry||'', size = j.companyStaffCountRange||'';
    const loc = j.location||j.geoLocationName||'';
    const emp = j.employmentType||j.type||'', desc = j.description||j.summary||'';
    const stAttrs = formatPeriodAttrs(j.start||j.startDate);
    const enAttrs = formatPeriodAttrs(j.end||j.endDate);

    parts.push(`    <position>`);
    parts.push(`      ${xmlTag('title', escapeXml(title))}`);
    if (emp) parts.push(`      ${xmlTag('employment_type', escapeXml(emp))}`);
    if (loc) parts.push(`      ${xmlTag('location', escapeXml(loc))}`);
    parts.push(`      <company>`);
    parts.push(`        ${xmlTag('name', escapeXml(cName))}`);
    if (cUser) parts.push(`        ${xmlTag('username', escapeXml(cUser))}`);
    if (cId) parts.push(`        ${xmlTag('id', escapeXml(String(cId)))}`);
    if (cUrl&&isValidUrl(cUrl)) parts.push(`        ${xmlTag('url', escapeXml(cUrl))}`);
    if (ind) parts.push(`        ${xmlTag('industry', escapeXml(ind))}`);
    if (size) parts.push(`        ${xmlTag('staff_count', escapeXml(size))}`);
    parts.push(`      </company>`);
    if (stAttrs) {
      parts.push(`      <period>`);
      parts.push(`        ${xmlSelfClose('start', stAttrs)}`);
      if (enAttrs) parts.push(`        ${xmlSelfClose('end', enAttrs)}`);
      parts.push(`      </period>`);
    }
    if (desc&&desc.trim()) parts.push(`      ${xmlTag('description', escapeXml(truncateText(decodeHtmlEntities(desc), CONFIG.TEXT_LIMITS.DESCRIPTION)))}`);
    parts.push(`    </position>`);
    parts.push(``);
  }
  // Remove trailing blank line
  if (parts.length > 1 && parts[parts.length - 1] === '') parts.pop();
  parts.push(`  </experience>`);
  return parts;
}

function buildEducation(educations) {
  const list = safeArray(educations);
  if (list.length === 0) return [];
  const parts = [`  <education>`];
  for (const e of list) {
    if (!e) continue;
    const name = safeString(e.schoolName||e.school?.name||e.institution||e.name, "School");
    const url = extractUrl(e.url||e.schoolUrl);
    const stYear = e.start?.year || e.startDate?.year;
    const enYear = e.end?.year || e.endDate?.year;
    const deg = safeString(e.degree||e.degreeName), field = safeString(e.fieldOfStudy||e.field||e.major);
    const grade = safeString(e.grade||e.gpa), desc = safeString(e.description||e.activities);
    parts.push(`    <school>`);
    parts.push(`      ${xmlTag('name', escapeXml(name))}`);
    if (url) parts.push(`      ${xmlTag('url', escapeXml(url))}`);
    if (field) parts.push(`      ${xmlTag('field_of_study', escapeXml(field))}`);
    parts.push(`      <degree>${escapeXml(deg)}</degree>`);
    if (grade) parts.push(`      ${xmlTag('grade', escapeXml(grade))}`);
    if (stYear||enYear) {
      parts.push(`      <period>`);
      if(stYear) parts.push(`        ${xmlSelfClose('start', {year: String(stYear)})}`);
      if(enYear) parts.push(`        ${xmlSelfClose('end', {year: String(enYear)})}`);
      parts.push(`      </period>`);
    }
    if (desc&&desc.trim()) parts.push(`      ${xmlTag('description', escapeXml(truncateText(decodeHtmlEntities(desc), CONFIG.TEXT_LIMITS.DESCRIPTION)))}`);
    parts.push(`    </school>`);
  }
  parts.push(`  </education>`);
  return parts;
}

function buildSkills(skills) {
  const list = safeArray(skills).sort((a,b)=>(b.endorsementsCount||b.endorsementCount||0)-(a.endorsementsCount||a.endorsementCount||0)).slice(0, CONFIG.MEDIA.MAX_SKILLS);
  if (list.length === 0) return [];
  const parts = [`  <skills>`];
  for (const s of list) {
    const name = safeString(s.name||s.skill||s.title); if (!name) continue;
    const end = s.endorsementsCount||s.endorsementCount||0;
    const attrs = {name: name}; if(end>0)attrs.endorsements=String(end);
    parts.push(`    ${xmlSelfClose('skill', attrs)}`);
  }
  parts.push(`  </skills>`);
  return parts;
}

function buildComment(comment, depth = 0, profileUsername = null) {
  if (!comment || depth > CONFIG.MEDIA.MAX_NESTED_REPLY_DEPTH) return [];
  const parts = [];
  // Base indent: 8 spaces for comment, +4 for each reply depth level (inside replies tag)
  const indent = '        ' + '    '.repeat(depth);
  const tag = depth === 0 ? 'comment' : 'reply';
  const aName = comment.author?.name || comment.authorName || "User";
  const aUser = comment.author?.username || '', aTitle = comment.author?.title || '';
  const aUrl = comment.author?.linkedinUrl || (aUser ? `https://www.linkedin.com/in/${aUser}` : '');
  const text = decodeHtmlEntities(comment.text || comment.content || "").trim();
  const createdAt = comment.createdAtString ? formatDateISO(comment.createdAtString.replace(' ', 'T') + 'Z') : null;
  const isEdited = comment.isEdited === true, isPinned = comment.isPinned === true;
  const rx = comment.totalSocialActivityCounts || {}, total = rx.totalReactionCount || 0;

  parts.push(`${indent}<${tag}${isPinned ? ' pinned="true"' : ''}>`);
  if (createdAt) parts.push(`${indent}  ${xmlTag('created_at', createdAt)}`);
  if (isEdited) parts.push(`${indent}  <is_edited>true</is_edited>`);
  if (text) parts.push(`${indent}  ${xmlTag('text', escapeXml(truncateText(replaceNewlinesWithTabs(text), CONFIG.TEXT_LIMITS.COMMENT), false))}`);
  parts.push(`${indent}  <author>`);

  // Author ordering: based on whether comment has replies, with profile owner variation for replies
  const hasReplies = comment.replies && comment.replies.length > 0;

  if (depth > 0) {
    // Reply ordering depends on if it's the profile owner vs other users
    const isProfileOwner = profileUsername && aUser === profileUsername;

    if (isProfileOwner) {
      // Profile owner reply: username, url, title, name
      if (aUser) parts.push(`${indent}    ${xmlTag('username', escapeXml(aUser))}`);
      if (aUrl) parts.push(`${indent}    ${xmlTag('url', escapeXml(aUrl))}`);
      if (aTitle) parts.push(`${indent}    ${xmlTag('title', escapeXml(aTitle, false))}`);
      parts.push(`${indent}    ${xmlTag('name', escapeXml(aName))}`);
    } else {
      // Other user reply: username, url, name, title
      if (aUser) parts.push(`${indent}    ${xmlTag('username', escapeXml(aUser))}`);
      if (aUrl) parts.push(`${indent}    ${xmlTag('url', escapeXml(aUrl))}`);
      parts.push(`${indent}    ${xmlTag('name', escapeXml(aName))}`);
      if (aTitle) parts.push(`${indent}    ${xmlTag('title', escapeXml(aTitle, false))}`);
    }
  } else {
    // Comment ordering: depends on whether comment has replies
    if (hasReplies) {
      // Comment with replies: username, url, name, title
      if (aUser) parts.push(`${indent}    ${xmlTag('username', escapeXml(aUser))}`);
      if (aUrl) parts.push(`${indent}    ${xmlTag('url', escapeXml(aUrl))}`);
      parts.push(`${indent}    ${xmlTag('name', escapeXml(aName))}`);
      if (aTitle) parts.push(`${indent}    ${xmlTag('title', escapeXml(aTitle, false))}`);
    } else {
      // Comment without replies: name, username, url, title
      parts.push(`${indent}    ${xmlTag('name', escapeXml(aName))}`);
      if (aUser) parts.push(`${indent}    ${xmlTag('username', escapeXml(aUser))}`);
      if (aUrl) parts.push(`${indent}    ${xmlTag('url', escapeXml(aUrl))}`);
      if (aTitle) parts.push(`${indent}    ${xmlTag('title', escapeXml(aTitle, false))}`);
    }
  }
  parts.push(`${indent}  </author>`);

  if (total > 0) {
    parts.push(`${indent}  <reactions total="${total}">`);
    // like, praise, empathy fixed; appreciation/interest sorted by count; funny last
      const intCount = rx.InterestCount || rx.interestCount || 0;
      const apprCount = rx.appreciationCount || 0;
      if (rx.likeCount > 0) parts.push(`${indent}    ${xmlTag('like', String(rx.likeCount))}`);
      if (rx.praiseCount > 0) parts.push(`${indent}    ${xmlTag('praise', String(rx.praiseCount))}`);
      if (rx.empathyCount > 0) parts.push(`${indent}    ${xmlTag('empathy', String(rx.empathyCount))}`);
      // appreciation/interest sorted by count descending
      if (apprCount >= intCount) {
        if (apprCount > 0) parts.push(`${indent}    ${xmlTag('appreciation', String(apprCount))}`);
        if (intCount > 0) parts.push(`${indent}    ${xmlTag('interest', String(intCount))}`);
      } else {
        if (intCount > 0) parts.push(`${indent}    ${xmlTag('interest', String(intCount))}`);
        if (apprCount > 0) parts.push(`${indent}    ${xmlTag('appreciation', String(apprCount))}`);
      }
      if (rx.funnyCount > 0) parts.push(`${indent}    ${xmlTag('funny', String(rx.funnyCount))}`);
    parts.push(`${indent}  </reactions>`);
  }

  const replies = safeArray(comment.replies).slice(0, CONFIG.MEDIA.MAX_REPLIES_PER_COMMENT);
  if (replies.length > 0) {
    parts.push(`${indent}  <replies>`);
    for (const r of replies) parts.push(...buildComment(r, depth + 1, profileUsername));
    parts.push(`${indent}  </replies>`);
  }
  parts.push(`${indent}</${tag}>`);
  return parts;
}

// Extract clean username from company mention (handles malformed publicIdentifier)
function extractCompanyUsername(c) {
  // If publicIdentifier looks like a URL, extract from actual url field
  if (c.publicIdentifier && c.publicIdentifier.includes('linkedin.com')) {
    if (c.url) {
      const match = c.url.match(/linkedin\.com\/(?:company|school|showcase)\/([^\/]+)/);
      if (match) return match[1];
    }
    return '';
  }
  return c.publicIdentifier || '';
}

// Deduplicate company mentions by id
function dedupeCompanyMentions(mentions) {
  const seen = new Set();
  return mentions.filter(c => {
    if (!c || !c.id) return false;
    if (seen.has(c.id)) return false;
    seen.add(c.id);
    return true;
  });
}

function buildPosts(posts, profileUsername = null) {
  if (!Array.isArray(posts) || posts.length === 0) return [];
  const toShow = posts.slice(0, CONFIG.MEDIA.MAX_POSTS_TO_SHOW);
  const parts = [`  <posts total="${posts.length}">`];

  for (const post of toShow) {
    if (!post) continue;
    const isReshare = post.reposted === true || post.reshared === true;
    const dateISO = formatDateISO(post.postedDate);
    let dateReadable = post.postedAt || "Recent";
    // Add " ago" suffix if not already present
    if (dateReadable && !dateReadable.endsWith(' ago')) {
      dateReadable = dateReadable + ' ago';
    }
    const resharedPost = post.resharedPost || post.originalPost;

    parts.push(`    <post${isReshare ? ' type="reshare"' : ''}>`);
    if (dateISO) parts.push(`      ${xmlTag('posted_at', dateISO)}`);
    parts.push(`      ${xmlTag('posted_at_readable', escapeXml(dateReadable))}`);

    // Main text
    const mainText = decodeHtmlEntities(post.text || post.commentary || post.content || "").trim();
    if (mainText) parts.push(`      ${xmlTag('text', escapeXml(truncateText(replaceNewlinesWithTabs(mainText), CONFIG.TEXT_LIMITS.FULL_POST), false))}`);

    // Engagement - reaction order: like, praise, empathy, appreciation, interest, funny
    const totalRx = post.totalReactionCount || 0;
    const like = post.likeCount || 0, appr = post.appreciationCount || 0, emp = post.empathyCount || 0;
    const praise = post.praiseCount || 0, interest = post.InterestCount || post.interestCount || 0;
    const funny = post.funnyCount || 0, commentsCount = post.commentsCount || post.numComments || 0;
    const reposts = post.repostsCount || post.sharesCount || 0;

    // Calculate missing reaction counts from total if needed
    const knownReactions = like + praise + appr + interest + funny;
    let calculatedEmp = emp;
    if (emp === null && totalRx > knownReactions) {
      calculatedEmp = totalRx - knownReactions;
    }

    parts.push(`      <engagement>`);
    if (totalRx > 0 || like > 0) {
      parts.push(`        <reactions total="${totalRx || (like+calculatedEmp+appr+praise+interest+funny)}">`);
      // like, praise, empathy fixed; appreciation/interest sorted by count; funny last
      if (like > 0) parts.push(`          ${xmlTag('like', String(like))}`);
      if (praise > 0) parts.push(`          ${xmlTag('praise', String(praise))}`);
      if (calculatedEmp > 0) parts.push(`          ${xmlTag('empathy', String(calculatedEmp))}`);
      // appreciation/interest sorted by count descending
      if (appr >= interest) {
        if (appr > 0) parts.push(`          ${xmlTag('appreciation', String(appr))}`);
        if (interest > 0) parts.push(`          ${xmlTag('interest', String(interest))}`);
      } else {
        if (interest > 0) parts.push(`          ${xmlTag('interest', String(interest))}`);
        if (appr > 0) parts.push(`          ${xmlTag('appreciation', String(appr))}`);
      }
      if (funny > 0) parts.push(`          ${xmlTag('funny', String(funny))}`);
      parts.push(`        </reactions>`);
    }
    if (commentsCount > 0) parts.push(`        ${xmlTag('comments_count', String(commentsCount))}`);
    if (reposts > 0) parts.push(`        ${xmlTag('reposts_count', String(reposts))}`);
    parts.push(`      </engagement>`);

    // Media: Video or Image - output BEFORE company mentions, but NOT if post has a valid article with link
    const article = post.article || post.linkedArticle;
    const hasArticle = article && article.link && isValidUrl(article.link);

    if (!hasArticle) {
      const vid = safeArray(post.video || post.videos)[0];
      const hasValidVideo = vid && ((vid.poster && vid.poster.trim()) || (vid.url && vid.url.trim()));

      // For images: only show if URL has doubled feedshare-shrink_800 pattern AND no person mentions
      const hasMentions = safeArray(post.mentions).some(m => m && (m.publicIdentifier || m.urn));
      const allImages = safeArray(post.images || post.image);
      const contentImage = !hasMentions && allImages.find(img => img && img.url && img.url.includes('/feedshare-shrink_800/feedshare-shrink_800/'));

      if (hasValidVideo) {
        parts.push(`      <media>`);
        parts.push(`        <video${vid.duration ? ` duration_ms="${vid.duration}"` : ''}>`);
        if (vid.poster && vid.poster.trim()) parts.push(`          ${xmlTag('poster', escapeXml(vid.poster))}`);
        if (vid.url && vid.url.trim()) parts.push(`          ${xmlTag('url', escapeXml(vid.url))}`);
        parts.push(`        </video>`);
        parts.push(`      </media>`);
      } else if (contentImage) {
        const imgAttrs = {};
        if (contentImage.width) imgAttrs.width = String(contentImage.width);
        if (contentImage.height) imgAttrs.height = String(contentImage.height);
        parts.push(`      <media>`);
        parts.push(`        ${xmlTag('image', escapeXml(contentImage.url), imgAttrs)}`);
        parts.push(`      </media>`);
      }
    }

    // Company mentions - deduplicated, with clean username extraction
    const compMentions = dedupeCompanyMentions(safeArray(post.companyMentions).filter(c => c && c.name));
    if (compMentions.length > 0) {
      parts.push(`      <company_mentions>`);
      for (const c of compMentions) {
        const username = extractCompanyUsername(c);
        parts.push(`        ${xmlSelfClose('company', { name: c.name, id: c.id || '', username: username, url: c.url || '' })}`);
      }
      parts.push(`      </company_mentions>`);
    }

    // Person mentions - with first_name/last_name attributes
    const mentions = safeArray(post.mentions).filter(m => m && (m.publicIdentifier || m.urn) && (m.firstName || m.lastName));
    if (mentions.length > 0) {
      parts.push(`      <mentions>`);
      for (const m of mentions) {
        parts.push(`        ${xmlSelfClose('person', { first_name: m.firstName || '', last_name: m.lastName || '', username: m.publicIdentifier || '' })}`);
      }
      parts.push(`      </mentions>`);
    }

    // Reshared post - BEFORE comments for reshares
    if (isReshare && resharedPost) {
      const hasPerson = resharedPost.author?.firstName || resharedPost.author?.lastName;
      const hasCompany = resharedPost.company?.name;
      const rpText = decodeHtmlEntities(resharedPost.text || resharedPost.commentary || resharedPost.content || "").trim();
      parts.push(`      <reshared_post>`);

      // Person author vs Company author - different structure
      if (hasPerson) {
        parts.push(`        <author>`);
        if (resharedPost.author?.firstName) parts.push(`          ${xmlTag('first_name', escapeXml(resharedPost.author.firstName))}`);
        if (resharedPost.author?.lastName) parts.push(`          ${xmlTag('last_name', escapeXml(resharedPost.author.lastName))}`);
        if (resharedPost.author?.username) parts.push(`          ${xmlTag('username', escapeXml(resharedPost.author.username))}`);
        if (resharedPost.author?.url) parts.push(`          ${xmlTag('url', escapeXml(resharedPost.author.url))}`);
        parts.push(`        </author>`);
      } else if (hasCompany) {
        parts.push(`        <company>`);
        parts.push(`          ${xmlTag('name', escapeXml(resharedPost.company.name))}`);
        if (resharedPost.company.url) parts.push(`          ${xmlTag('url', escapeXml(resharedPost.company.url))}`);
        parts.push(`        </company>`);
      }

      if (rpText) parts.push(`        ${xmlTag('text', escapeXml(truncateText(replaceNewlinesWithTabs(rpText), CONFIG.TEXT_LIMITS.RESHARED_POST), false))}`);
      // Video in reshare
      const rpVid = safeArray(resharedPost.video || resharedPost.videos)[0];
      if (rpVid && (rpVid.poster || rpVid.url)) {
        parts.push(`        <video${rpVid.duration ? ` duration_ms="${rpVid.duration}"` : ''}>`);
        if (rpVid.poster) parts.push(`          ${xmlTag('poster', escapeXml(rpVid.poster))}`);
        if (rpVid.url) parts.push(`          ${xmlTag('url', escapeXml(rpVid.url))}`);
        parts.push(`        </video>`);
      }
      parts.push(`      </reshared_post>`);
    }

    // Article - output BEFORE comments
    const art = post.article || post.linkedArticle;
    if (art && art.link && isValidUrl(art.link)) {
      parts.push(`      <article>`);
      if (art.title) parts.push(`        ${xmlTag('title', escapeXml(art.title))}`);
      if (art.subtitle) parts.push(`        ${xmlTag('subtitle', escapeXml(art.subtitle))}`);
      parts.push(`        ${xmlTag('link', escapeXml(art.link))}`);
      parts.push(`      </article>`);
    }

    // Comments - with total/showing attrs only when showing less than available
    const allPostComments = safeArray(post.comments || post.topComments);
    const postComments = allPostComments.slice(0, CONFIG.MEDIA.MAX_TOP_COMMENTS);
    if (postComments.length > 0) {
      const totalAvail = allPostComments.length;
      const showing = postComments.length;
      // Only add total/showing attrs if we're showing fewer than available
      if (showing < totalAvail) {
        parts.push(`      <comments total="${totalAvail}" showing="${showing}">`);
      } else {
        parts.push(`      <comments>`);
      }
      for (const c of postComments) parts.push(...buildComment(c, 0, profileUsername));
      parts.push(`      </comments>`);
    }

    parts.push(`    </post>`);
    // Add blank line between posts (but not after the last one)
    if (toShow.indexOf(post) < toShow.length - 1) {
      parts.push(``);
    }
  }
  parts.push(`  </posts>`);
  return parts;
}

// Helper: Build complete XML document from profile data
function buildXmlDocument(profile, rootData, posts) {
  const allPositions = profile.fullPositions || profile.position || profile.positions || [];
  const profileUsername = profile.username || profile.publicIdentifier;
  const parts = ['<linkedin_profile>'];
  parts.push(...buildXmlMetadata());
  parts.push('');
  parts.push(...buildProfile(profile, rootData));
  parts.push('');
  parts.push(...buildExperience(allPositions));
  parts.push('');
  parts.push(...buildEducation(profile.educations || profile.education));
  parts.push('');
  parts.push(...buildSkills(profile.skills));
  parts.push('');
  parts.push(...buildPosts(posts, profileUsername));
  parts.push('</linkedin_profile>');
  return parts.join('\n');
}

// === MAIN EXECUTION ===
// Process a single item and return result object
function processItem(rawData, idx, isN8n = false) {
  try {
    const validation = validateDataStructure(rawData);
    if (!validation.valid) {
      const errResult = { error: validation.error, extracted_content: `<error>${escapeXml(validation.error)}</error>`, success: false, item_index: idx };
      return isN8n ? { json: errResult, pairedItem: { item: idx } } : errResult;
    }

    const { profile, posts } = validation;
    const rootData = rawData.data || rawData;
    const xmlContent = buildXmlDocument(profile, rootData, posts);
    const metadata = buildJsonMetadata(profile, rootData, posts, []);

    const result = { extracted_content: xmlContent, metadata, item_index: idx, success: true };
    return isN8n ? { json: result, pairedItem: { item: idx } } : result;
  } catch (error) {
    const errResult = { error: `Fatal: ${error.message}`, extracted_content: `<error>${escapeXml(error.message)}</error>`, success: false, item_index: idx };
    return isN8n ? { json: errResult, pairedItem: { item: idx } } : errResult;
  }
}

// n8n environment
if (typeof $input !== 'undefined') {
  const allItems = $input.all();
  if (!allItems || allItems.length === 0) {
    return [{ json: { error: "No input", extracted_content: '<error>No data</error>', success: false }, pairedItem: { item: 0 } }];
  }
  return allItems.map((item, idx) => processItem(item.json, idx, true));
}

// Plain Node.js (CLI testing)
try {
  const fs = require('fs');
  const inputData = JSON.parse(fs.readFileSync(0, 'utf8'));
  const items = Array.isArray(inputData) ? inputData : [inputData];
  const results = items.map((item, idx) => processItem(item, idx, false));
  if (results.length > 0) console.log(results[0].extracted_content);
} catch (e) {
  console.error(`Error: ${e.message}`);
  process.exit(1);
}
